function c(){s=$('[name="string"]').val();l=s.length;s=s.split("");for(var e=0;e<l;e++){if(/[a-f0-9]/i.test(s[e])==0){s[e]=0}}s=s.join("");if((l+1)%3==0){s+="0"}else if((l+2)%3==0){s+="00"}l=s.length;if(l==3){s=s.replace(/([a-f0-9])/ig,"$1$1")}sl=s.length/3;if(l>3){ss=[s.substring(0,2),s.substring(sl,sl+2),s.substring(sl*2,sl*2+2)]}else{return s}return ss.join("")}function h(e){bi=parseInt(e,16);return[bi>>16&255,bi>>8&255,bi&255]}function u(){v=c($("#string").val());$(".classy").css("background-color","#"+v).html(v);rgb=h(v);if(rgb[0]<128&&rgb[1]<128&&rgb[2]<128){$(".classy").css("color","rgba(255,255,255,.4)")}else{$(".classy").css("color","rgba(0,0,0,.2)")}}$(function(){u();$(".button").click(function(){u()});$(window).keydown(function(e){if(e.keyCode==13){e.preventDefault();u();return false}})})